import React, { Component } from 'react';
import './App.css';
import image from './images/bannerOne.jpg';

class App extends Component {
	state = {
		data: null,
		products: null,
		brands: null,
		allProducts: null,
		numProducts: null
	};

	componentDidMount() {
		//call fetch once component mounts
		this.callBackendAPI()
		.then( res => this.setState({
			//collectionName: res.express[0]['collectionName'],
			//productName: res.express[0]['productName']
			data: res[0],
			products: res[1],
			brands: res[2],
			allProducts: res[1],
			numProducts: res[3]
		}))
		.catch(err => console.log(err));
	}

	onClick(a){
		console.log(a);
		let val = [];
		if(a!=="all") {
			 val = this.fd(a);
		}
		else {
			 val = this.state['allProducts'];
		}
		this.setState({
			products: val
		})
	}

	fd(product) {
		let table = []
		let data = this.state['data']
		let len = 0;
		try {
			len = data.length;
			this.setState({
				numProducts: len
			})
		}
		catch(err){
			console.log("no tu");
		}
		for(let i = 0; i < len; i++) {
			if (data[i]['collectionName'] === product) {
				var collectionName = data[i]['collectionName'];
				var productName = data[i]['productName'];
				var imgSrc = data[i]['imageSrc'];
				var oldPrice = data[i]['oldPrice'];
				var newPrice = data[i]['newPrice'];
				if(newPrice !== "") {
					table.push(
					<div className="product-box">

						<div>
							<img src={imgSrc} alt={productName} width="100" height="100"/> 
						</div>

						<div className="product-info">
						<br/>		
								<b>{collectionName} </b>
						<br/>
								{productName}
						<br/>
								<strike>{oldPrice}</strike> <b style={{color:"#DC143C"}}>{newPrice}</b>
					</div>

			</div>
			);
			}
			else {
			table.push(
			<div className="product-box">
					<div>
							<img src={imgSrc} alt={productName} width="100" height="100"/> 
					</div>

					<div className="product-info">
						<br/>		
								<b>{collectionName}</b> 
						<br/>
								{productName}
						<br/>
								{oldPrice} 
					</div>

			</div>
			);
		}
		}
		}
		return table;
	}

	callBackendAPI = async() => {
		const response = await fetch('/express_backend');
		const body = await response.json();

		if(response.status !== 200) {
			throw Error(body.message);
		}

		let table = [];
		let exist = {};
		let productBrands = [];

		for(let  i = 0; i < body.express.length; i++) {
			var collectionName = body.express[i]['collectionName'];
			var productName = body.express[i]['productName'];
			var imgSrc = body.express[i]['imageSrc'];
			var oldPrice = body.express[i]['oldPrice'];
			var newPrice = body.express[i]['newPrice'];
			//append product name if it doesnt exist in the dict
			if(!exist[collectionName]) {
				exist[collectionName] = true;
				productBrands.push(
					<p>	
						<button onClick={() => this.onClick(body.express[i]['collectionName'])}>
						{collectionName}
						</button>
					</p>

				);
			}
			if(newPrice !== "") {
			table.push(
			<div className="product-box">

					<div>
							<img src={imgSrc} alt={productName} width="100" height="100"/> 
					</div>

					<div className="product-info">
						<br/>		
								<b>{collectionName} </b>
						<br/>
								{productName}
						<br/>
								<strike>{oldPrice}</strike> <b style={{color:"#DC143C"}}>{newPrice}</b>
					</div>

			</div>
			);
			}
			else {
			table.push(
			<div className="product-box">

					<div>
							<img src={imgSrc} alt={productName} width="100" height="100"/> 
					</div>

					<div className="product-info">
						<br/>		
								<b>{collectionName}</b>
						<br/>
								{productName}
						<br/>
								{oldPrice} 
					</div>

			</div>
			);
		}
			//children.push(<td>{`Column ${j + 1}`}</td>)
		}

		//append all parts
		var all = []
		all.push(body.express);
		all.push(table);
		all.push(productBrands);
		all.push(body.express.length);
		return all;
	};

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <p>
 						<b>BEAUTY BABES</b>						
						<br/>
						<font size="2">Makeup Lovers on a Budget</font>
          </p>
				</header>

				{/* navbar */}
				<div className="nav-bar nav-links">
					<a href="localhost:3000/" className="nav-links">
					<button onClick={() =>this.onClick('all')}>All Products</button>
					</a>

					<a href="localhost:3000/" className="nav-links">
						Eyes 
					</a> 
					<a href="localhost:3000/" className="nav-links">
						Lips
					</a>

					<a href="localhost:3000/" className="nav-links">
						Face
					</a>

					<a href="localhost:3000/" className="nav-links">
						SkinCare 
					</a>

					<a href="localhost:3000/" className="nav-links">
						Beauty Tools 
					</a>

					<a href="localhost:3000/" className="nav-links">
						Nails
					</a>
					<a href="localhost:3000/" className="nav-links">
						Palette & Sets 
					</a>

				</div>

				<img src={image} alt="banner One" className="banner"/> 

				{/* PRODUCT DIVS */}
				<div className="items-div">

					{/* filters */}
					<div className="filters-div">
						<b style={{'fontSize':'13px'}}>Filter By Brand</b>
						<br/>
						<font size="1" color="#88888">{this.state.brands}</font>
					</div>

					<div>
						<div className="product-header">
							<b style={{'fontSize':'9px'}}> <font color="#880000">{this.state.numProducts}</font> PRODUCTS FOUND </b>
						</div>

						<div style={{'display': 'flex ','flexDirection':'row', 'flexWrap': 'wrap' , 'fontSize': '9px', 'jusfiyContent':'center'}}>
							{this.state.products}
						</div>
					</div>

				</div>

     </div>
    );
  }
}

export default App;
